// Note that the algorithm provided below assumes a 1 second iteration time
// Justin Angevaare
// May 2018

// Set temperature
msg.temperature =  msg.payload;

// Well that node doesn't work... do manually
msg.interval = 2000

// Calculate error
msg.error = flow.get('rims_target') - msg.temperature;

// Get output max
if(global.get('rims_limiter')){
    msg.max_output = flow.get('rims_max')}else {
        msg.max_output = 100}

// Calculate integrator max
msg.integrator_max = msg.max_output/flow.get('i');

// Update integrator
// For purposes of integrator, bound error by [-1, 1]
msg.integrator = flow.get('integrator');
msg.integrator += (msg.interval/1000) * Math.max(Math.min(msg.error, 1), -1);

// Bound total integrator by absolute maximum
msg.integrator = Math.max(Math.min(msg.integrator, msg.integrator_max), -msg.integrator_max);

// Output updated integrator to flow context
flow.set('integrator', msg.integrator);

// Calculate proportional action
msg.output = msg.error * flow.get('p');

// Calculate intergral action
msg.output += msg.integrator * flow.get('i');

// Calculate derivative action
msg.derivative = (msg.error - flow.get('last_error'))/(msg.interval/1000);
msg.output += msg.derivative * flow.get('d');

// Bound output by [0, max_output]
msg.output = Math.max(Math.min(msg.output, msg.max_output), 0);

// Output updated last_error to flow context
msg.last_error = msg.error;
flow.set('last_error', msg.last_error);

// Set payload to element output
msg.payload = msg.output;
return msg;

